import { BigNumber, utils } from "ethers";
/**
 * Formats a wallet address and returns the sliced string (eg: `0xBfb...2bAA `)
 * @param {string} address - The wallet address
 * @return {string} - The formatted address
 */
export const formatAddress = (address) => {
  if (address && address.length > 0) {
    const padding = 5;
    const length = address.length;
    const slice1 = address.substring(0, padding);
    const slice2 = address.substring(length - (padding - 1), length);
    return `${slice1}...${slice2}`;
  }
  else {
    return address;
  }
};
export const formatBalance = (balanceInWei) => {
  return parseFloat(utils.formatEther(BigNumber.from(balanceInWei))).toFixed(4);
};
